definition.script:
node{
   stage('SCM Checkout'){
       git credentialsId: 'git-credentials', url: 'https://github.com/pranay1404/myApp'
   }
   stage('Mvn Package'){
     def mvnHome = tool name: 'maven-3', type: 'maven'
     def mvnCMD = "${mvnHome}/bin/mvn"
     sh "${mvnCMD} clean package"
   }
   stage('Build Docker Image'){
     sh 'docker build -t pranay1404/appdeploy:1.0.0 .'
   }
   stage('Push Docker Image'){
     withCredentials([string(credentialsId: 'docker-pwd', variable: 'dockerHubPwd')]) {
        sh "docker login -u pranay1404 -p ${dockerHubPwd}"
     }
     sh 'docker push pranay1404/appdeploy:1.0.0'
   }

}
