definition.script:
node {
  stage 'Checkout'
  git 'https://github.com/irwin-tech/docker-pipeline-demo.git'

  stage 'Docker build'
  docker.build('myapp')

  stage 'Docker push'
  docker.withRegistry('https://xxxxxxxxxxxx.dkr.ecr.us-east-1.amazonaws.com', 'ecr:us-east-1:ecr-access') {
    docker.image('myapp').push('latest')
  }
}
